import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { Card, CardTitle, Table, FormGroup, Label, Input } from "reactstrap";

import Form from "react-bootstrap/Form";
import Modal from 'react-bootstrap/Modal';
import Button from "react-bootstrap/Button";
import Col from 'react-bootstrap/Col';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';

import Select from 'react-select';
import makeAnimated from 'react-select/animated';

import { useNavigate } from "react-router-dom";
import UsePagination from "../../hooks/pagination";
import "../../assets/scss/course.scss"

const Course = () => {
    const [course, setCourse] = useState()
    const [checkeds, setCheckeds] = useState([])
    const navigate = useNavigate();
    const [page, setPage] = useState(0)
    const [size, setSize] = useState(5)
    const [isSort, setIsSort] = useState(false)
    const [modalShow, setModalShow] = React.useState(false);


    // call api 
    const getApiCourse = useCallback(async () => {
        try {
            const res = await axios.get(`http://localhost:8080/api/v1/course/getPage?page=${page}&&size=${size}`);
            const data = res.data ? res.data : []
            setCourse(data)
        } catch (e) {

        }
    }, [page, size])

    useEffect(() => {
        getApiCourse();
    }, [getApiCourse])


    // chuyển đến trang chi tiết khóa học
    const openDetail = (id) => {
        navigate(`${id}`);
    }

    // xử lý checked
    const handleChecked = (id) => {
        let copy = [...checkeds]
        const index = copy.indexOf(id)
        if (index === -1) {
            copy.push(id)
            setCheckeds((prev) => [...prev, id])
        } else {
            copy.splice(index, 1)
            setCheckeds([...copy])
        }
    }

    const handleCheckedAll = (checked) => {
        setCheckeds([])
        if (checked) {
            course.content.map(item => (
                setCheckeds((prev) => [...prev, item.id])
            ))
        }
    }

    const sortData = (id) => {
        if (!id) return
        setIsSort(!isSort)
        if (!isSort) {
            sortDataGraduallySmaller(id)
        } else {
            sortDataAscending(id)
        }
    }

    const sortDataAscending = (data) => {
        let newData = [...course.content]
        if (!newData?.map(p => p[data])) return
        newData?.sort((a, b) => a[data] > b[data] ? 1 : -1)
        setCourse((prev) => ({ ...prev, content: newData }))
    }

    const sortDataGraduallySmaller = (data) => {
        let newData = [...course.content]
        if (!newData?.map(p => p[data])) return
        newData?.sort((a, b) => a[data] < b[data] ? 1 : -1)
        setCourse((prev) => ({ ...prev, content: newData }))
    }

    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        setValidated(true);
    };


    return (
        <div>
            <Card>
                <CardTitle tag="h6" className="border-bottom p-3 mb-0 course-cart-header">
                    <Form className="d-flex search-data">
                        <Form.Control
                            type="search"
                            placeholder="Search"
                            className="me-2"
                            aria-label="Search"
                        />
                        <Button variant="outline-success">Search</Button>
                    </Form>
                    <div className="box-action">
                        {checkeds.length > 0 &&
                            <div className="box-delete">
                                <i className="bi bi-archive"></i>
                                Xóa
                            </div>
                        }
                        <div className="box-add" onClick={() => setModalShow(true)}>
                            <i class="bi bi-plus-circle"></i>
                            Thêm
                        </div>
                    </div>
                </CardTitle>
                {course &&
                    <>
                        <Table className="no-wrap mt-3 align-middle" responsive borderless>
                            <thead>
                                <tr>
                                    <th>
                                        <FormGroup check>
                                            <Input type="checkbox" onChange={(e) => handleCheckedAll(e.target.checked)} /> <Label check>Code</Label>
                                        </FormGroup>
                                    </th>
                                    <th>Tên</th>
                                    <th>Giới thiệu</th>
                                    <th onClick={() => sortData('price')}>Giá</th>
                                    <th>Đang học</th>
                                    <th>Đăng ký</th>
                                    <th>Thời gian</th>
                                </tr>
                            </thead>
                            <tbody>
                                {course.content.map(item => (
                                    <tr key={item.id} className="border-top">
                                        <td>
                                            <FormGroup check>
                                                <Input type="checkbox" onChange={() => { handleChecked(item.id) }} checked={checkeds.includes(item.id)} /> <Label check>{item.code}</Label>
                                            </FormGroup>
                                        </td>
                                        <td>
                                            {item.name}
                                        </td>
                                        <td>
                                            {item.introduce}
                                        </td>
                                        <td>
                                            {item.price}
                                        </td>
                                        <td>
                                            {item.numberOfStudent}
                                        </td>
                                        <td>
                                            {item.numberOfPurchases}
                                        </td>
                                        <td>
                                            {item.totalCourseDuration}
                                        </td>
                                        <td>
                                            <svg onClick={() => openDetail(item.id)} style={{ cursor: "pointer" }} xmlns="http://www.w3.org/2000/svg" height="20px" viewBox="0 -960 960 960" width="20px" fill="#666666"><path d="M216-144q-29.7 0-50.85-21.15Q144-186.3 144-216v-528q0-30.11 21-51.56Q186-817 216-816h346l-72 72H216v528h528v-274l72-72v346q0 29.7-21.15 50.85Q773.7-144 744-144H216Zm264-336Zm-96 96v-153l354-354q11-11 24-16t26.5-5q14.4 0 27.45 5 13.05 5 23.99 15.78L891-840q11 11 16 24.18t5 26.82q0 13.66-5.02 26.87-5.02 13.2-15.98 24.13L537-384H384Zm456-405-51-51 51 51ZM456-456h51l231-231-25-26-26-25-231 231v51Zm257-257-26-25 26 25 25 26-25-26Z" /></svg>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                        <UsePagination
                            totalPages={course.totalPages}
                            number={course.number + 1}
                            setPage={setPage}
                        />
                    </>
                }
            </Card>
            <Modal
                show={modalShow}
                onHide={() => setModalShow(false)}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Thêm khóa học
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form noValidate validated={validated} onSubmit={handleSubmit}>
                        <Row className="mb-3">
                            <Form.Group as={Col} md="6" controlId="validationCustom01">
                                <Form.Label>Tên khóa học</Form.Label>
                                <Form.Control
                                    required
                                    type="text"
                                    placeholder="Tên khóa học"
                                    defaultValue=""
                                />
                                <Form.Control.Feedback type="invalid">Vui lòng nhập tên khóa học</Form.Control.Feedback>
                            </Form.Group>
                            <Form.Group as={Col} md="6" controlId="validationCustom02">
                                <Form.Label>Giới thiệu khóa học</Form.Label>
                                <Form.Control
                                    required
                                    type="text"
                                    placeholder=""
                                    defaultValue=""
                                />
                                <Form.Control.Feedback type="invalid">Vui lòng nhập giới thiệu của khóa học</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Row className="mb-3">
                            <Form.Group as={Col} md="6" controlId="validationCustom03">
                                <Form.Label>Giá khóa học</Form.Label>
                                <Form.Control type="number" required />
                                <Form.Control.Feedback type="invalid">
                                    Vui lòng nhập giá khóa học
                                </Form.Control.Feedback>
                            </Form.Group>
                            <Form.Group as={Col} md="6" controlId="validationCustom04">
                                <Form.Label>Thời lượng khóa học</Form.Label>
                                <Form.Control type="number" required />
                                <Form.Control.Feedback type="invalid">
                                    Vui lòng nhập thời lượng khóa học
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Select
                            closeMenuOnSelect={false}
                            components={animatedComponents}
                            isMulti
                            options={colourOptions}
                        />
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={() => setModalShow(false)}>Thêm</Button>
                    <Button onClick={() => setModalShow(false)}>Đóng</Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default Course;
