import React, { useState, useEffect, useCallback } from "react";

import { Card, CardBody } from "reactstrap";
import Form from "react-bootstrap/Form";
import Modal from 'react-bootstrap/Modal';
import Button from "react-bootstrap/Button";
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import { useParams } from "react-router-dom";
import axios from "axios";

export default function CourseDetail() {
    const [validated, setValidated] = useState(false);
    const { course_id } = useParams()
    const [course, SetCourse] = useState()

    const getCourse = useCallback(async () => {
        const res = await axios.get(`http://localhost:8080/api/v1/course/find/${course_id}`)
        SetCourse(res.data)
    }, [course_id])

    useEffect(() => {
        getCourse()
    }, [getCourse])

    return (
        <Card>
            <CardBody>
                {course &&
                    <Form noValidate validated={validated} id='create-course-form'>
                        <Row>
                            <Col md="4">
                                <input style={{
                                    border: "1px solid",
                                    width: "100%",
                                    borderRadius: "5px",
                                    display: 'none'
                                }}
                                    id="input-file"
                                    onChange={(e) => setFile(e.target.files[0])} type="file" s
                                />
                                <div className="course-img" onClick={() => inputFile()} style={{ backgroundImage: `url(${file ? URL.createObjectURL(file) : course.imageCourse})` }} />
                            </Col>
                            <Col md="8">
                                <Row className="mb-3">
                                    <Col md="5">
                                        <Form.Label>Mã khóa học: {course.code}</Form.Label>
                                    </Col>
                                    <Col md="3">
                                        <Form.Label>Số học viên: {course.numberOfStudent}</Form.Label>
                                    </Col>
                                    <Col md="4">
                                        <Form.Label style={{ float: 'right' }}>Số lượng mua hàng: {course.numberOfPurchases}</Form.Label>
                                    </Col>
                                </Row>
                                <Row className="mb-3">
                                    <Form.Group as={Col} md="6" controlId="validationCustom01">
                                        <Form.Label>Tên khóa học</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            placeholder="Tên khóa học"
                                            // onChange={(e) => handleInput('name', e.target.value)}
                                            value={course.name ? course.name : ""}
                                        />
                                        <Form.Control.Feedback type="invalid">Vui lòng nhập tên khóa học</Form.Control.Feedback>
                                    </Form.Group>
                                    <Form.Group as={Col} md="6" controlId="validationCustom03">
                                        <Form.Label>Giá khóa học</Form.Label>
                                        <Form.Control type="number" required
                                            // onChange={(e) => handleInput('price', e.target.value)}
                                            value={course.price ? course.price : ""}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            Vui lòng nhập giá khóa học
                                        </Form.Control.Feedback>
                                    </Form.Group>
                                </Row>
                                <Row className="mb-3">
                                    <Form.Group as={Col} md="6" controlId="validationCustom02">
                                        <Form.Label>Giới thiệu khóa học</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            placeholder=""
                                            // onChange={(e) => handleInput('introduce', e.target.value)}
                                            value={course.introduce ? course.introduce : ""}
                                        />
                                        <Form.Control.Feedback type="invalid">Vui lòng nhập giới thiệu của khóa học</Form.Control.Feedback>
                                    </Form.Group>
                                    <Form.Group as={Col} md="6" controlId="validationCustom04">
                                        <Form.Label>Thời lượng khóa học</Form.Label>
                                        <Form.Control type="number" required
                                            // onChange={(e) => handleInput('totalCourseDuration', e.target.value)}
                                            value={course.totalCourseDuration ? course.totalCourseDuration : ""}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            Vui lòng nhập thời lượng khóa học
                                        </Form.Control.Feedback>
                                    </Form.Group>
                                </Row>
                                <Form.Group controlId="validationCustom04">
                                    <Form.Label>Chọn môn học cho khóa học</Form.Label>
                                    {/* <Select
                                    closeMenuOnSelect={false}
                                    components={animatedComponents}
                                    isMulti
                                    options={subjects}
                                    onChange={(e) => handleSelect(e)}
                                    value={course.courseSubjects}
                                    required
                                /> */}
                                    <Form.Control.Feedback type="invalid">
                                        Vui lòng chọn môn học cho khóa học
                                    </Form.Control.Feedback>
                                </Form.Group>
                            </Col>
                        </Row>
                    </Form>
                }
            </CardBody>
        </Card>
    )
}
